// src/VoiceControl.js
import React, { useEffect } from 'react';
import { createAssistantClient } from '@salutejs/client';

const VoiceControl = ({ onStart, onPause, onStop }) => {
  useEffect(() => {
    // Инициализация клиента с необходимыми параметрами
    try {
      const client = createAssistantClient({
        
        // Добавьте другие параметры, если они требуются
      });

      if (!client) {
        throw new Error('Ошибка: клиент не был создан.');
      }

      // Обработчик команд
      client.on('command', (command) => {
        console.log('Received command:', command);

        switch (command.command) {
          case 'play':
            onStart(); // Начало игры
            break;
          case 'pause':
            onPause(); // Пауза игры
            break;
          case 'stop':
            onStop(); // Остановка игры
            break;
          default:
            console.warn(`Неизвестная команда: ${command.command}`);
            break;
        }
      });

      // Запуск клиента
      client.start();

      // Очистка при размонтировании компонента
      return () => {
        client.stop();
      };

    } catch (error) {
      console.error('Произошла ошибка при создании клиента:', error);
    }
  }, [onStart, onPause, onStop]);

  return null;
};

export default VoiceControl;
