import React, { useEffect } from 'react';
import { createAssistantClient } from '@salutejs/client';

const VoiceControl = ({ onStart, onPause, onStop }) => {
  useEffect(() => {
    // Создание клиента для взаимодействия с голосовыми командами
    const client = createAssistantClient({
      meta: {
        appName: 'PongGame',  // Название приложения
        dubbing: 'en',        // Например, язык озвучивания
      },
    });

    if (!client) {
      console.error("Ошибка: клиент не был создан.");
      return;
    }

    // Обработчик голосовых команд
    const handleCommand = (command) => {
      if (!command) {
        console.error("Ошибка: команда не определена.");
        return;
      }

      switch (command.command) {
        case 'play':
          onStart();  // Начало игры
          break;
        case 'pause':
          onPause();  // Пауза игры
          break;
        case 'stop':
          onStop();  // Остановка игры
          break;
        default:
          break;
      }
    };

    // Привязка обработчика команд
    client.on('command', handleCommand);

    // Старт клиента
    client.start();

    // Очистка при размонтировании компонента
    return () => {
      client.off('command', handleCommand);  // Отписка от события команд
      client.stop();  // Остановка клиента
    };
  }, [onStart, onPause, onStop]);

  return null;
};

export default VoiceControl;
