import React, {
  useRef,
  useEffect,
  useState,
  useCallback,
} from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';

const initializeAssistant = (getState, getRecoveryState) => {
  if (process.env.NODE_ENV === 'development') {
    return createSmartappDebugger({
      token: process.env.REACT_APP_TOKEN ?? '',
      initPhrase: `–ó–∞–ø—É—Å—Ç–∏ ${process.env.REACT_APP_SMARTAPP}`,
      getState,
      nativePanel: {
        defaultText: '',
        screenshotMode: false,
        tabIndex: -1,
      },
    });
  } else {
    return createAssistant({ getState });
  }
};

export class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            notes: [{ id: Math.random().toString(36).substring(7), title: '—Ç–µ—Å—Ç' }],
            player: { x: 1, y: 1 },
            enemy: { x: 11, y: 12 },
            maze: this.generateMaze(), // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ª–∞–±–∏—Ä–∏–Ω—Ç–∞
            showHelpModal: false,
        };

        this.assistant = initializeAssistant(() => this.getStateForAssistant());

        this.assistant.on('data', (event) => {
            console.log(`assistant.on(data)`, event);
            if (event.type === 'character') {
                console.log(`assistant.on(data): character: "${event?.character?.id}"`);
            } else if (event.type === 'insets') {
                console.log(`assistant.on(data): insets`);
            } else if (event.type === 'navigation' && event.navigation?.command) {
                // –û–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞–≤–∏–≥–∞—Ü–∏–æ–Ω–Ω–æ–π –∫–æ–º–∞–Ω–¥—ã –∫–∞–∫ –¥–µ–π—Å—Ç–≤–∏—è
                const navCommand = event.navigation.command.toLowerCase(); // –Ω–∞–ø—Ä–∏–º–µ—Ä, 'RIGHT' ‚Üí 'right'
                this.dispatchAssistantAction({ type: navCommand });
            } else {
                const { action } = event;
                this.dispatchAssistantAction(action);
            }
        });

        this.assistant.on('start', (event) => {
            let initialData = this.assistant.getInitialData();
            console.log(`assistant.on(start)`, event, initialData);
        });

        this.assistant.on('command', (event) => {
            console.log(`assistant.on(command)`, event);
        });

        this.assistant.on('error', (event) => {
            console.log(`assistant.on(error)`, event);
        });

        this.assistant.on('tts', (event) => {
            console.log(`assistant.on(tts)`, event);
        });
    }

    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ª–∞–±–∏—Ä–∏–Ω—Ç–∞
    generateMaze() {
        console.log('Generating maze...');
        const maze = Array.from({ length: rows }, () => Array(cols).fill(1));

        // –ú–µ—Ç–æ–¥ "–î–µ—Ä–µ–≤–æ –±–∏–Ω–∞—Ä–Ω–æ–≥–æ" –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –ø—É—Ç–µ–π
        for (let y = 1; y < rows; y += 2) {  // –†–∞–±–æ—Ç–∞–µ–º —Ç–æ–ª—å–∫–æ —Å –Ω–µ—á–µ—Ç–Ω—ã–º–∏ —Å—Ç—Ä–æ–∫–∞–º–∏
            for (let x = 1; x < cols; x += 2) {  // –†–∞–±–æ—Ç–∞–µ–º —Ç–æ–ª—å–∫–æ —Å –Ω–µ—á–µ—Ç–Ω—ã–º–∏ —Å—Ç–æ–ª–±—Ü–∞–º–∏
                maze[y][x] = 0;  // –î–µ–ª–∞–µ–º –∫–ª–µ—Ç–∫—É –ø—Ä–æ—Ö–æ–¥–∏–º–æ–π (0)

                const neighbors = [];
                if (y > 1) neighbors.push([0, -1]);  // –ï—Å—Ç—å –∫–ª–µ—Ç–∫–∞ –≤—ã—à–µ
                if (x > 1) neighbors.push([-1, 0]);  // –ï—Å—Ç—å –∫–ª–µ—Ç–∫–∞ —Å–ª–µ–≤–∞

