                    const [dx, dy] = neighbors[Math.floor(Math.random() * neighbors.length)];
                    maze[y + dy][x + dx] = 0;  // –î–µ–ª–∞–µ–º —Å–æ—Å–µ–¥–Ω—é—é –∫–ª–µ—Ç–∫—É –ø—Ä–æ—Ö–æ–¥–∏–º–æ–π
                }
            }
        }

        // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω–∞—á–∞–ª—å–Ω—É—é –∏ –∫–æ–Ω–µ—á–Ω—É—é —Ç–æ—á–∫–∏ –ø—Ä–æ—Ö–æ–¥–∏–º—ã–º–∏
        maze[1][1] = 0;  // –ù–∞—á–∞–ª—å–Ω–∞—è –ø–æ–∑–∏—Ü–∏—è
        maze[rows - 2][cols - 2] = 0;  // –ö–æ–Ω–µ—á–Ω–∞—è —Ç–æ—á–∫–∞

        console.log('Generated maze:', maze);
        return maze;
    }


    // –ü—Ä–æ–≤–µ—Ä–∫–∞ —Å–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –∞—Å—Å–∏—Å—Ç–µ–Ω—Ç–∞
    getStateForAssistant() {
        console.log('getStateForAssistant: this.state:', this.state);
        const state = {
            item_selector: {
                items: this.state.notes.map(({ id, title }, index) => ({
                    number: index + 1,
                    id,
                    title,
                })),
            },
        };
        console.log('getStateForAssistant: state:', state);
        return state;
    }

    // –î–≤–∏–≥–∞–µ–º –∏–≥—Ä–æ–∫–∞
    movePlayer(direction) {
        const { x, y } = this.state.player;
        let newPlayerPosition = { x, y };
        const maze = this.state.maze;

        switch (direction) {
            case 'up':
                if (y > 0 && maze[y - 1][x] === 0) newPlayerPosition = { x, y: y - 1 };
                break;
            case 'down':
                if (y < rows - 1 && maze[y + 1][x] === 0) newPlayerPosition = { x, y: y + 1 };
                break;
            case 'left':
                if (x > 0 && maze[y][x - 1] === 0) newPlayerPosition = { x: x - 1, y };
                break;
            case 'right':
                if (x < cols - 1 && maze[y][x + 1] === 0) newPlayerPosition = { x: x + 1, y };
                break;
            default:
                break;
        }

        console.log('Moving player to:', newPlayerPosition);
        this.setState({ player: newPlayerPosition }, () => {
            //–ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–±–µ–¥—ã –ø–æ—Å–ª–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è
            if (newPlayerPosition.x === cols - 2 && newPlayerPosition.y === rows - 2) {
                this.generateAndStart();
            }
            if (
                newPlayerPosition.x === this.state.enemy.x &&
                newPlayerPosition.y === this.state.enemy.y
            ) {
                const newMaze = this.generateMaze();
                this.setState({
                    maze: newMaze,
                    player: { x: 1, y: 1 },
                    enemy: { x: 10, y: 12 }
                });
            }
        });
    }
    moveEnemy(direction) {
        const { x, y } = this.state.enemy;
        let newEnemyPosition = { x, y };
        const maze = this.state.maze;

        switch (direction) {
            case 'up':
                if (y > 0 && maze[y - 1][x] === 0) newEnemyPosition = { x, y: y - 1 };
                break;
            case 'down':
                if (y < rows - 1 && maze[y + 1][x] === 0) newEnemyPosition = { x, y: y + 1 };
                break;
            case 'left':
                if (x > 0 && maze[y][x - 1] === 0) newEnemyPosition = { x: x - 1, y };
                break;
            case 'right':
                if (x < cols - 1 && maze[y][x + 1] === 0) newEnemyPosition = { x: x + 1, y };
                break;
            default:
                break;
        }

        console.log('Moving enemy to:', newEnemyPosition);
        this.setState({ enemy: newEnemyPosition }, () => {
            if (newEnemyPosition.x === cols - 2 && newEnemyPosition.y === rows - 2) {
                this.generateAndStart();
            }
            if (this.state.enemy.x === this.state.player.x && this.state.enemy.y === this.state.player.y) {
                const newMaze = this.generateMaze();

                this.setState({
                    maze: newMaze,
                    player: { x: 1, y: 1 },
                    enemy: { x: 10, y: 12 }
                });
            }

        });
    }
    startEnemyMovement() {
        this.enemyMovementInterval = setInterval(() => {
            const availableDirections = this.getAvailableDirections(this.state.enemy); // –ü–æ–ª—É—á–∞–µ–º –¥–æ—Å—Ç—É–ø–Ω—ã–µ –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏—è
            let bestDirection = this.getBestDirection(availableDirections);

            if (bestDirection) {
                this.moveEnemy(bestDirection);
            }

            if (this.state.enemy.x === this.state.player.x && this.state.enemy.y === this.state.player.y) {
                const newMaze = this.generateMaze();

                this.setState({
                    maze: newMaze,
                    player: { x: 1, y: 1 },
                    enemy: { x: 10, y: 12 }
                });
            }
        }, 500);
    }
    componentDidMount() {
        window.addEventListener('keydown', this.handleKeyDown);
        this.startEnemyMovement();
    }

    componentWillUnmount() {
        window.removeEventListener('keydown', this.handleKeyDown);
    }

    handleKeyDown = (event) => {
        switch (event.key) {
            case 'ArrowUp':
                this.movePlayer('up');
                break;
            case 'ArrowDown':
                this.movePlayer('down');
                break;
            case 'ArrowLeft':
                this.movePlayer('left');
                break;
            case 'ArrowRight':
                this.movePlayer('right');
                break;
            case 'h':
            case 'H':
                this.showHelp();
                break;
            default:
                break;
        }
    };


    showHelp = () => {
        this.setState({ showHelpModal: true });
    };

    closeHelp = () => {
        this.setState({ showHelpModal: false });
    };



    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –Ω–æ–≤–æ–≥–æ –ª–∞–±–∏—Ä–∏–Ω—Ç–∞ –∏ –Ω–∞—á–∞–ª–æ –∏–≥—Ä—ã
    generateAndStart = () => {
        console.log('Generating new maze and restarting...');
        this.setState({ maze: this.generateMaze(), player: { x: 1, y: 1 }, enemy: { x: 10, y: 12 } });
    };

    // –î–∏—Å–ø–µ—Ç—á–µ—Ä –∫–æ–º–∞–Ω–¥ –∞—Å—Å–∏—Å—Ç–µ–Ω—Ç–∞
    dispatchAssistantAction(action) {
        console.log('dispatchAssistantAction', action);
        if (action) {
            switch (action.type) {
                case 'help':
                    this.showHelp();
                    break


                default:
                    console.log('Unknown action type:', action.type);
                    throw new Error();
            }
        }
    }




    // –ú–µ—Ç–æ–¥ –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏–π –≤—Ä–∞–≥–∞
    getAvailableDirections(enemy) {
        const { x, y } = enemy;
        const maze = this.state.maze;
        const availableDirections = [];

        if (y > 0 && maze[y - 1][x] === 0) availableDirections.push('up');
        if (y < rows - 1 && maze[y + 1][x] === 0) availableDirections.push('down');
        if (x > 0 && maze[y][x - 1] === 0) availableDirections.push('left');
        if (x < cols - 1 && maze[y][x + 1] === 0) availableDirections.push('right');

        return availableDirections;
    }

    // –ú–µ—Ç–æ–¥ –¥–ª—è –≤—ã–±–æ—Ä–∞ –Ω–∞–∏–ª—É—á—à–µ–≥–æ –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏—è –¥–ª—è –≤—Ä–∞–≥–∞
    getBestDirection(availableDirections) {
        const { x, y } = this.state.enemy;
        const player = this.state.player;

        // –î–ª—è –∫–∞–∂–¥–æ–π –¥–æ—Å—Ç—É–ø–Ω–æ–π –ø–æ–∑–∏—Ü–∏–∏ —Å—á–∏—Ç–∞–µ–º, –∫–∞–∫–æ–µ –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –ø—Ä–∏–≤–µ–¥–µ—Ç –∫ –∏–≥—Ä–æ–∫—É
        let bestDirection = null;
        let minDistance = Infinity;

        availableDirections.forEach(direction => {
            let newX = x;
            let newY = y;

            if (direction === 'up') newY -= 1;
            if (direction === 'down') newY += 1;
            if (direction === 'left') newX -= 1;
            if (direction === 'right') newX += 1;

            // –°—á–∏—Ç–∞–µ–º —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–µ –æ—Ç –≤—Ä–∞–≥–∞ –¥–æ –∏–≥—Ä–æ–∫–∞
            const distance = Math.abs(newX - player.x) + Math.abs(newY - player.y);

            // –í—ã–±–∏—Ä–∞–µ–º –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å –º–∏–Ω–∏–º–∞–ª—å–Ω—ã–º —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–µ–º
            if (distance < minDistance) {
                minDistance = distance;
                bestDirection = direction;
            }
        });

        return bestDirection;
    }
    render() {
        return (
            <div>
                <MazeCanvas
                    maze={this.state.maze}
                    player={this.state.player}
                    enemy={this.state.enemy}
                />

                {this.state.showHelpModal && (
                    <div className="modal-backdrop">
                        <div className="modal-content">
                            <h2>–ü–æ–º–æ—â—å</h2>
                            <pre>
                                {`üîπ –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å—Ç—Ä–µ–ª–æ—á–∫–∞–º–∏:
‚Äî –í–≤–µ—Ä—Ö: "–≤–≤–µ—Ä—Ö"
‚Äî –í–Ω–∏–∑: "–≤–Ω–∏–∑"
‚Äî –í–ª–µ–≤–æ: "–≤–ª–µ–≤–æ"
‚Äî –í–ø—Ä–∞–≤–æ: "–≤–ø—Ä–∞–≤–æ"

üîπ –¶–µ–ª—å:
‚Äî –î–æ–±–µ—Ä–∏—Ç–µ—Å—å –¥–æ –≤—ã—Ö–æ–¥–∞ –∏–∑ –ª–∞–±–∏—Ä–∏–Ω—Ç–∞
  –≤ –ø—Ä–∞–≤–æ–º –Ω–∏–∂–Ω–µ–º —É–≥–ª—É.

üîπ –ì–æ–ª–æ—Å–æ–º:
‚Äî –ú–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –≥–æ–ª–æ—Å–æ–≤—ã–µ –∫–æ–º–∞–Ω–¥—ã:
"–≤–≤–µ—Ä—Ö", "–≤–Ω–∏–∑", "–≤–ª–µ–≤–æ", "–≤–ø—Ä–∞–≤–æ", "–ø–æ–º–æ—â—å".`}
                            </pre>
                            <button onClick={this.closeHelp}>–ó–∞–∫—Ä—ã—Ç—å</button>
                        </div>
                    </div>
                )}
            </div>
        );
    }
};

export default Pong;
