// src/VoiceControl.js
import React, { useEffect } from 'react';
import { createAssistantClient } from '@salutejs/client';

const VoiceControl = () => {
  const [client, setClient] = useState(null);

  useEffect(() => {
    // Инициализация клиента (например, для голосового управления)
    const initClient = async () => {
      try {
        // Ваш код для создания клиента
        const newClient = await createClient(); // Предположим, что createClient() — это ваша функция
        setClient(newClient);
      } catch (error) {
        console.error('Произошла ошибка при создании клиента:', error);
      }
    };

    initClient();
  }, []);

  // Проверяем, что client и dubbing существуют, перед их использованием
  const handleVoiceAction = () => {
    if (client && client.dubbing) {
      // Ваш код для использования client.dubbing
    } else {
      console.log('Ошибка: client или client.dubbing не определены');
    }
  };

  return (
    <div>
      <h1>Pong with Voice Control</h1>
      <button onClick={handleVoiceAction}>Использовать голосовое управление</button>
    </div>
  );
};

export default VoiceControl;
