import React, { useRef, useEffect, useState } from 'react';

const Pong = () => {
  const canvasRef = useRef(null);
  const [difficulty, setDifficulty] = useState('easy');
  const [gameOver, setGameOver] = useState(false);
  const [playerScore, setPlayerScore] = useState(0);
  const [botScore, setBotScore] = useState(0);

  const playerY = useRef(150);
  const [playerYState, setPlayerYState] = useState(150); // для рендера

  const ball = useRef({
    x: 300,
    y: 200,
    dx: 4,
    dy: 4,
    radius: 7,
  });

  const bot = useRef({
    x: 580,
    y: 150,
    width: 10,
    height: 80,
  });

  const ballSpeed = useRef(4);
  const botSpeed = useRef(2);
  const animationRef = useRef(null);

  const resetBall = () => {
    ball.current.x = 300;
    ball.current.y = 200;
    const speed = ballSpeed.current;
    ball.current.dx = (Math.random() > 0.5 ? 1 : -1) * speed;
    ball.current.dy = (Math.random() > 0.5 ? 1 : -1) * speed;
  };

  const updateBot = () => {
    const center = bot.current.y + bot.current.height / 2;
    const target = ball.current.y;
    if (center < target - 10) bot.current.y += botSpeed.current;
    else if (center > target + 10) bot.current.y -= botSpeed.current;
    bot.current.y = Math.max(0, Math.min(400 - bot.current.height, bot.current.y));
  };

  const update = () => {
    const b = ball.current;
    b.x += b.dx;
    b.y += b.dy;

    // Collision with top and bottom
    if (b.y - b.radius < 0 || b.y + b.radius > 400) {
      b.dy *= -1;
    }

    updateBot();

    // Player collision
    if (b.x - b.radius < 30 &&
        b.y > playerY.current &&
        b.y < playerY.current + 80) {
      b.dx *= -1;
    }

    // Bot collision
    if (
      b.x + b.radius > bot.current.x &&
      b.y > bot.current.y &&
      b.y < bot.current.y + bot.current.height
    ) {
      b.dx *= -1;
    }

    // Score
    if (b.x < 0) {
      setBotScore((s) => s + 1);
      resetBall();
    }
    if (b.x > 600) {
      setPlayerScore((s) => s + 1);
      resetBall();
    }
  };

  const render = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillStyle = 'white';

    // Player
    ctx.fillRect(20, playerY.current, 10, 80);

    // Bot
    ctx.fillRect(bot.current.x, bot.current.y, bot.current.width, bot.current.height);

    // Ball
    ctx.beginPath();
    ctx.arc(ball.current.x, ball.current.y, ball.current.radius, 0, Math.PI * 2);
    ctx.fill();

    // Score
    ctx.font = '30px Arial';
    ctx.fillText(playerScore, 200, 50);
    ctx.fillText(botScore, 400, 50);

    if (gameOver) {
      ctx.fillText('Game Over', 230, 200);
      const winner = playerScore > botScore ? 'Player Wins!' : 'Bot Wins!';
      ctx.fillText(winner, 210, 240);
    }
  };

  const animate = () => {
    update();
    render();
    if (!gameOver) {
      animationRef.current = requestAnimationFrame(animate);
    }
  };

  // Перезапускаем игру
  const startGame = (level) => {
    cancelAnimationFrame(animationRef.current);
    setDifficulty(level);
    setPlayerScore(0);
    setBotScore(0);
    setGameOver(false);

    // Устанавливаем параметры сложности
    ballSpeed.current = level === 'hard' ? 7 : 4;
    botSpeed.current = level === 'hard' ? 6 : 2;

    playerY.current = 150;
    bot.current.y = 150;
    resetBall();
    animate();
  };

  // Проверка окончания игры
  useEffect(() => {
    if (playerScore >= 10 || botScore >= 10) {
      setGameOver(true);
    }
  }, [playerScore, botScore]);

  // Перезапуск анимации после снятия gameOver
  useEffect(() => {
    if (!gameOver) {
      animationRef.current = requestAnimationFrame(animate);
    }
    return () => cancelAnimationFrame(animationRef.current);
  }, [gameOver]);

  const handleKeyDown = (e) => {
    const speed = 20;
    if (e.key === 'ArrowUp') {
      playerY.current = Math.max(0, playerY.current - speed);
    } else if (e.key === 'ArrowDown') {
      playerY.current = Math.min(400 - 80, playerY.current + speed);
    }
    setPlayerYState(playerY.current); // принудительный ререндер
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <div className="game-container">
      <h2>Pong Game</h2>
      <canvas ref={canvasRef} width="600" height="400" style={{ background: 'black' }} />
      <div style={{ marginTop: '10px' }}>
        <button onClick={() => startGame('easy')}>Easy</button>
        <button onClick={() => startGame('hard')}>Hard</button>
        {gameOver && (
          <button onClick={() => startGame(difficulty)}>Restart</button>
        )}
      </div>
    </div>
  );
};

export default Pong;
