// src/VoiceControl.js
import React, { useEffect } from 'react';
import { createSaluteClient } from '@salutejs/client';

const VoiceControl = ({ onStart, onPause, onStop }) => {
  useEffect(() => {
    // Создание клиента
    const client = createSaluteClient();

    // Обработка полученных голосовых команд
    client.on('voice_command', (command) => {
      console.log('Получена команда:', command);

      switch (command.type) {
        case 'play':
          onStart();  // Начало игры
          break;
        case 'pause':
          onPause();  // Пауза игры
          break;
        case 'stop':
          onStop();  // Остановка игры
          break;
        default:
          console.log('Неизвестная команда');
      }
    });

    // Запуск клиента
    client.start();

    // Очистка при размонтировании компонента
    return () => {
      client.stop();
    };
  }, [onStart, onPause, onStop]);

  return null;
};

export default VoiceControl;
