import React, { useEffect, useRef, useState } from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';
import './App.css';

const Pong = () => {
  const canvasRef = useRef(null);
  const [difficulty, setDifficulty] = useState('easy');
  const [gameOver, setGameOver] = useState(false);

  const ballSpeed = difficulty === 'hard' ? 7 : 4;
  const botSpeed = difficulty === 'hard' ? 6 : 2;

  let player = { x: 20, y: 150, width: 10, height: 80, score: 0 };
  let bot = { x: 570, y: 150, width: 10, height: 80, score: 0 };
  let ball = { x: 300, y: 200, radius: 7, dx: 4, dy: 4 };

  const resetBall = () => {
    ball.x = 300;
    ball.y = 200;
    ball.dx = (Math.random() > 0.5 ? 1 : -1) * ballSpeed;
    ball.dy = (Math.random() > 0.5 ? 1 : -1) * ballSpeed;
  };

  const updateBot = () => {
    const center = bot.y + bot.height / 2;
    const target = ball.y;

    if (Math.random() < (difficulty === 'easy' ? 0.2 : 0)) {
      return;
    }

    if (center < target - 10) bot.y += botSpeed;
    else if (center > target + 10) bot.y -= botSpeed;

    bot.y = Math.max(0, Math.min(400 - bot.height, bot.y));
  };

  const update = () => {
    ball.x += ball.dx;
    ball.y += ball.dy;

    if (ball.y < 0 || ball.y > 400) ball.dy = -ball.dy;

    updateBot();

    if (
      ball.x < player.x + player.width &&
      ball.y > player.y && ball.y < player.y + player.height
    ) {
      ball.dx = -ball.dx;
    }

    if (
      ball.x > bot.x &&
      ball.y > bot.y && ball.y < bot.y + bot.height
    ) {
      ball.dx = -ball.dx;
    }

    if (ball.x < 0) {
      bot.score++;
      resetBall();
    }
    if (ball.x > 600) {
      player.score++;
      resetBall();
    }

    if (player.score >= 10 || bot.score >= 10) {
      setGameOver(true);
    }
  };

  const render = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    ctx.fillStyle = 'white';
    ctx.fillRect(player.x, player.y, player.width, player.height);
    ctx.fillRect(bot.x, bot.y, bot.width, bot.height);
    ctx.beginPath();
    ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
    ctx.fill();

    ctx.font = '30px Arial';
    ctx.fillText(player.score, canvas.width / 4, 50);
    ctx.fillText(bot.score, 3 * canvas.width / 4, 50);

    if (gameOver) {
      const winner = player.score > bot.score ? 'Player Wins!' : 'Bot Wins!';
      ctx.fillText('Game Over', 250, 200);
      ctx.fillText(winner, 250, 240);
    }
  };

  const gameLoop = () => {
    if (gameOver) return;

    update();
    render();
    requestAnimationFrame(gameLoop);
  };

  useEffect(() => {
    resetBall();
    gameLoop();
  }, [difficulty, gameOver]);

  const handleKeyDown = (e) => {
    const speed = 20;
    if (e.key === 'ArrowUp') {
      player.y = Math.max(0, player.y - speed);
    }
    if (e.key === 'ArrowDown') {
      player.y = Math.min(400 - player.height, player.y + speed);
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const changeDifficulty = (level) => {
    setDifficulty(level);
  };

  return (
    <div className="game-container">
      <h1>Pong Game</h1>
      <canvas ref={canvasRef} width="600" height="400" />
      <div>
        <button onClick={() => changeDifficulty('easy')}>Easy</button>
        <button onClick={() => changeDifficulty('hard')}>Hard</button>
      </div>
      {gameOver && (
        <div>
          <button onClick={() => setGameOver(false)}>Restart</button>
        </div>
      )}
    </div>
  );
};

export default Pong;
