import React from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';
import './App.css';
import MazeCanvas from './components/MazeCanvas';

const canvas = document.getElementById('gameCanvas');
const ctx = canvas.getContext('2d');



let player = { x: 20, y: canvas.height / 2 - 40, width: 10, height: 80, score: 0 };
let bot = { x: canvas.width - 30, y: canvas.height / 2 - 40, width: 10, height: 80, score: 0 };
let ball = { x: canvas.width / 2, y: canvas.height / 2, radius: 7, dx: 5, dy: 5 };
let ballSpeed = 2;
let botSpeed = 3;

let difficulty = 'easy'; // easy, hard
let gameOver = false;

const initializeAssistant = (getState, getRecoveryState) => {
    if (process.env.NODE_ENV === 'development') {
        return createSmartappDebugger({
            token: 'eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiJ9..Nv0fmm6qQ7Ubwr85nTmvKXQxKt1_ICgPy02vvp3bdhP0JBERQxD48IfnHyA9KhW_A0XTK7Lyck00qMNZCLi9c74Whx0inqnI9uSJO9vs1IHZW8_K15oN7_YBj2kefEtZyEb4npXyl7tJzKBvPI7pmzAaKCTKKUPxbGwGCJdP7aXimNkIdCaLShf978NdA5fEbqUA5UWslMaFhXul2bsOFBaBVPlSBbq6D2_CoBObJ0RU8KblT1KPajebJtJb3NZYhAsMIzHaETJPzR0utlSFuCTE-a1CEMS2S9CYIueCXDq2cY5cUtRD2McqUORcefGVkv1UeYtbV5jYnb-UGiz5RhKxOlie-FIhI5FcOaYsCcVVA4tEK3eEDiucHtHCweM85lnDr54QfFZbTiAxS6wb_F0rHT_-hv85gObG7Zvca-3zQJdh-YyA0zX5YQGeUmVVRFnMRLBRHhLZWadgsXBVbLmM_emIO4Ovh9WTYtZ45IzB7UUAbThpTpk7NOTx6dg62-z5RRnvocuFUMJPb6c3qE7V88Mr5Rey1P_LKhkmFUjbW4oq-Zsv3Vmg0tolTIZ39d-9Y9JJHlALQESkEaEzwJnAgeXC0oMIiVoeL5MYtIW_mLnjOnp4lSbbqAnd6M6DOgm1aObQuuoTnhn3llaDTh7apI61NQVqoaYzqnJACAI',
            initPhrase: `Запусти Pong`,
            getState,
            nativePanel: {
                defaultText: '',
                screenshotMode: false,
                tabIndex: -1,
            },
        });
    } else {
        return createAssistant({ getState });
    }
};


this.assistant = initializeAssistant(() => this.getStateForAssistant());

this.assistant.on('data', (event) => {
    console.log(`assistant.on(data)`, event);

    if (event.type === 'navigation' && event.navigation?.command) {
        const navCommand = event.navigation.command.toLowerCase();
        this.dispatchAssistantAction({ type: navCommand });
    } else {
        const { action } = event;
        this.dispatchAssistantAction(action);
    }
});








function drawRect(x, y, w, h, color) {
    ctx.fillStyle = color;
    ctx.fillRect(x, y, w, h);
}

function drawCircle(x, y, r, color) {
    ctx.fillStyle = color;
    ctx.beginPath();
    ctx.arc(x, y, r, 0, Math.PI * 2, false);
    ctx.closePath();
    ctx.fill();
}

function drawText(text, x, y, color) {
    ctx.fillStyle = color;
    ctx.font = "30px Arial";
    ctx.fillText(text, x, y);
}

function resetBall() {
    ball.x = canvas.width / 2;
    ball.y = canvas.height / 2;
    ball.dx = (Math.random() > 0.5 ? 1 : -1) * ballSpeed;
    ball.dy = (Math.random() > 0.5 ? 1 : -1) * ballSpeed;
}

function updateBot() {
    const center = bot.y + bot.height / 2;
    const target = ball.y;

    // При easy бот иногда "ошибается"
    let missChance = difficulty === 'easy' ? 0.2 : 0;

    if (Math.random() < missChance) {
        return; // бот пропускает движение
    }

    if (center < target - 10) bot.y += botSpeed;
    else if (center > target + 10) bot.y -= botSpeed;

    // Защита от выхода за границы
    bot.y = Math.max(0, Math.min(canvas.height - bot.height, bot.y));
}


function update() {
    if (gameOver) return;

    ball.x += ball.dx;
    ball.y += ball.dy;

    if (ball.y < 0 || ball.y > canvas.height) ball.dy = -ball.dy;

    updateBot();

    if (ball.x < player.x + player.width &&
        ball.y > player.y && ball.y < player.y + player.height) {
        ball.dx = -ball.dx;
    }

    if (ball.x > bot.x &&
        ball.y > bot.y && ball.y < bot.y + bot.height) {
        ball.dx = -ball.dx;
    }

    if (ball.x < 0) {
        bot.score++;
        resetBall();
    }
    if (ball.x > canvas.width) {
        player.score++;
        resetBall();
    }

    if (player.score >= 10 || bot.score >= 10) {
        gameOver = true;
    }
}

function render() {
    drawRect(0, 0, canvas.width, canvas.height, 'black');
    drawRect(player.x, player.y, player.width, player.height, 'white');
    drawRect(bot.x, bot.y, bot.width, bot.height, 'white');
    drawCircle(ball.x, ball.y, ball.radius, 'white');
    drawText(player.score, canvas.width / 4, 50, 'white');
    drawText(bot.score, 3 * canvas.width / 4, 50, 'white');

    if (gameOver) {
        const winner = player.score > bot.score ? "Player Wins!" : "Bot Wins!";
        drawText("Game Over", canvas.width / 2 - 100, canvas.height / 2, 'red');
        drawText(winner, canvas.width / 2 - 100, canvas.height / 2 + 40, 'yellow');
    }
}

function gameLoop() {
    update();
    render();
    requestAnimationFrame(gameLoop);
}

document.addEventListener('keydown', function(e) {
    const speed = 20;
    if (e.key === 'ArrowUp') {
        player.y = Math.max(0, player.y - speed);
    }
    if (e.key === 'ArrowDown') {
        player.y = Math.min(canvas.height - player.height, player.y + speed);
    }
});


window.addEventListener('message', function(event) {
    const action = event.data?.action;
    if (!action) return;
    switch (action.type) {
        case 'up':
            player.y = Math.max(0, player.y - 20);
            break;
        case 'down':
            player.y = Math.min(canvas.height - player.height, player.y + 20);
            break;
        case 'easy':
            difficulty = 'easy';
            ballSpeed = 2;
            botSpeed = 2; // медленный бот, может не успеть
            break;
        case 'hard':
            difficulty = 'hard';
            ballSpeed = 7;
            botSpeed = 6; // быстрый бот
            break;
        case 'start':
            if (gameOver) {
                player.score = 0;
                bot.score = 0;
                gameOver = false;
            }
            break;
    }
});


gameLoop();

export default Pong;
