import React, { useRef, useEffect, useState } from 'react';

const Pong = () => {
  const canvasRef = useRef(null);
  const [ballSpeedX, setBallSpeedX] = useState(4);
  const [ballSpeedY, setBallSpeedY] = useState(4);
  const [ballPosX, setBallPosX] = useState(50);
  const [ballPosY, setBallPosY] = useState(50);
  const [paddle1Y, setPaddle1Y] = useState(150);
  const [paddle2Y, setPaddle2Y] = useState(150);
  const [score1, setScore1] = useState(0);
  const [score2, setScore2] = useState(0);
  const [difficulty, setDifficulty] = useState('easy'); // 'easy' or 'hard'

  const paddleHeight = 100;
  const paddleSpeed = 30;
  const botSpeed = difficulty === 'easy' ? 3 : 6; // Bot speed depends on difficulty

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const interval = setInterval(updateGame, 1000 / 60); // 60 FPS

    function updateGame() {
      moveBall();
      detectCollisions();
      moveBotPaddle();
      draw();
    }

    function moveBall() {
      if (ballPosX + ballSpeedX > canvas.width || ballPosX + ballSpeedX < 0) {
        if (ballPosX + ballSpeedX > canvas.width) {
          setScore1(prev => prev + 1);
        } else {
          setScore2(prev => prev + 1);
        }
        setBallSpeedX(prev => -prev);
      }

      if (ballPosY + ballSpeedY > canvas.height || ballPosY + ballSpeedY < 0) {
        setBallSpeedY(prev => -prev); // Ball bounces off top and bottom walls
      }

      setBallPosX(prev => prev + ballSpeedX);
      setBallPosY(prev => prev + ballSpeedY);
    }

    function detectCollisions() {
      // Left paddle (Player 1)
      if (ballPosX < 10 && ballPosY > paddle1Y && ballPosY < paddle1Y + paddleHeight) {
        setBallSpeedX(prev => -prev);
      }

      // Right paddle (Bot or Player 2)
      if (ballPosX > canvas.width - 10 && ballPosY > paddle2Y && ballPosY < paddle2Y + paddleHeight) {
        setBallSpeedX(prev => -prev);
      }
    }

    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw ball
      ctx.beginPath();
      ctx.arc(ballPosX, ballPosY, 10, 0, Math.PI * 2);
      ctx.fill();

      // Draw paddles
      ctx.fillStyle = 'white';
      ctx.fillRect(0, paddle1Y, 10, paddleHeight); // Left paddle
      ctx.fillRect(canvas.width - 10, paddle2Y, 10, paddleHeight); // Right paddle (Bot)

      // Draw scores
      ctx.font = '30px Arial';
      ctx.fillText(score1, canvas.width / 4, 30);
      ctx.fillText(score2, (canvas.width / 4) * 3, 30);
    }

    function movePaddle(event) {
      if (event.key === 'ArrowUp' && paddle2Y > 0) {
        setPaddle2Y(prev => prev - paddleSpeed);
      }
      if (event.key === 'ArrowDown' && paddle2Y + paddleHeight < canvas.height) {
        setPaddle2Y(prev => prev + paddleSpeed);
      }
      if (event.key === 'w' && paddle1Y > 0) {
        setPaddle1Y(prev => prev - paddleSpeed);
      }
      if (event.key === 's' && paddle1Y + paddleHeight < canvas.height) {
        setPaddle1Y(prev => prev + paddleSpeed);
      }
    }

    // Bot paddle AI (simple following)
    function moveBotPaddle() {
      if (ballPosY > paddle2Y + paddleHeight / 2) {
        setPaddle2Y(prev => Math.min(prev + botSpeed, canvas.height - paddleHeight));
      } else if (ballPosY < paddle2Y + paddleHeight / 2) {
        setPaddle2Y(prev => Math.max(prev - botSpeed, 0));
      }
    }

    window.addEventListener('keydown', movePaddle);

    return () => {
      clearInterval(interval);
      window.removeEventListener('keydown', movePaddle);
    };
  }, [ballPosX, ballPosY, ballSpeedX, ballSpeedY, paddle1Y, paddle2Y, score1, score2, difficulty]);

  return (
    <div>
      <canvas ref={canvasRef} width="800" height="600" tabIndex="0" />
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <button onClick={() => setDifficulty('easy')}>Easy</button>
        <button onClick={() => setDifficulty('hard')}>Hard</button>
      </div>
    </div>
  );
};

export default Pong;
