import React, { useRef, useEffect, useState } from 'react';

const Pong = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);

  const [difficulty, setDifficulty] = useState('easy');
  const [gameOver, setGameOver] = useState(false);
  const [playerScore, setPlayerScore] = useState(0);
  const [botScore, setBotScore] = useState(0);
  const [winner, setWinner] = useState('');

  const playerY = useRef(150);
  const [playerYState, setPlayerYState] = useState(150);

  const ball = useRef({
    x: 300,
    y: 200,
    dx: 4,
    dy: 4,
    radius: 7,
  });

  const bot = useRef({
    x: 580,
    y: 150,
    width: 10,
    height: 80,
  });

  const getBallSpeed = () => (difficulty === 'hard' ? 7 : 4);
  const getBotSpeed = () => (difficulty === 'hard' ? 6 : 2);

  const resetBall = () => {
    const speed = getBallSpeed();
    const angle = Math.random() * Math.PI / 2 - Math.PI / 4;
    const direction = Math.random() > 0.5 ? 1 : -1;

    ball.current.x = 300;
    ball.current.y = 200;
    ball.current.dx = Math.cos(angle) * speed * direction;
    ball.current.dy = Math.sin(angle) * speed;
  };

  const updateBot = () => {
    const center = bot.current.y + bot.current.height / 2;
    const target = ball.current.y;
    const botSpeed = getBotSpeed();

    if (center < target - 10) bot.current.y += botSpeed;
    else if (center > target + 10) bot.current.y -= botSpeed;

    bot.current.y = Math.max(0, Math.min(400 - bot.current.height, bot.current.y));
  };

  const update = () => {
    const b = ball.current;
    b.x += b.dx;
    b.y += b.dy;

    if (b.y - b.radius < 0 || b.y + b.radius > 400) {
      b.dy *= -1;
    }

    updateBot();

    // Player collision
    if (b.x - b.radius < 30 && b.y > playerY.current && b.y < playerY.current + 80) {
      b.dx = Math.abs(b.dx);
    }

    // Bot collision
    if (
      b.x + b.radius > bot.current.x &&
      b.y > bot.current.y &&
      b.y < bot.current.y + bot.current.height
    ) {
      b.dx = -Math.abs(b.dx);
    }

    // Score
    if (b.x < 0) {
      setBotScore(prev => {
        const newScore = prev + 1;
        if (newScore >= 10) {
          setGameOver(true);
          setWinner('Bot Wins!');
        }
        return newScore;
      });
      resetBall();
    } else if (b.x > 600) {
      setPlayerScore(prev => {
        const newScore = prev + 1;
        if (newScore >= 10) {
          setGameOver(true);
          setWinner('Player Wins!');
        }
        return newScore;
      });
      resetBall();
    }
  };

  const render = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillStyle = 'white';

    // Player
    ctx.fillRect(20, playerY.current, 10, 80);

    // Bot
    ctx.fillRect(bot.current.x, bot.current.y, bot.current.width, bot.current.height);

    // Ball
    ctx.beginPath();
    ctx.arc(ball.current.x, ball.current.y, ball.current.radius, 0, Math.PI * 2);
    ctx.fill();

    // Score
    ctx.font = '30px Arial';
    ctx.fillText(`${playerScore}`, 200, 50);
    ctx.fillText(`${botScore}`, 400, 50);

    // Game over
    if (gameOver) {
      ctx.font = '30px Arial';
      ctx.fillText('Game Over', 220, 180);
      ctx.fillText(winner, 210, 220);
    }
  };

  const gameLoop = () => {
    update();
    render();
    if (!gameOver) {
      animationRef.current = requestAnimationFrame(gameLoop);
    }
  };

  const startGame = (level) => {
    cancelAnimationFrame(animationRef.current); // остановить предыдущий
    setDifficulty(level);
    setPlayerScore(0);
    setBotScore(0);
    setWinner('');
    setGameOver(false);
    bot.current.y = 150;
    playerY.current = 150;
    resetBall();
    animationRef.current = requestAnimationFrame(gameLoop);
  };

  useEffect(() => {
    animationRef.current = requestAnimationFrame(gameLoop);
    return () => cancelAnimationFrame(animationRef.current);
  }, []);

  const handleKeyDown = (e) => {
    const speed = 20;
    if (e.key === 'ArrowUp') {
      playerY.current = Math.max(0, playerY.current - speed);
    } else if (e.key === 'ArrowDown') {
      playerY.current = Math.min(400 - 80, playerY.current + speed);
    }
    setPlayerYState(playerY.current); // принудительное обновление
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <div className="game-container">
      <h2>Pong Game</h2>
      <canvas
        ref={canvasRef}
        width="600"
        height="400"
        style={{ background: 'black', display: 'block', margin: 'auto' }}
      />
      <div style={{ marginTop: '10px', textAlign: 'center' }}>
        <button onClick={() => startGame('easy')}>Easy</button>
        <button onClick={() => startGame('hard')}>Hard</button>
        {gameOver && <button onClick={() => startGame(difficulty)}>Restart</button>}
      </div>
    </div>
  );
};

export default Pong;
