// src/VoiceControl.js
import React, { useEffect } from 'react';
import { createSaluteClient } from '@salutejs/client';

const VoiceControl = ({ onStart, onPause, onStop }) => {
  useEffect(() => {
    const client = createSaluteClient();

    // Обработчик голосовых команд
    client.on('voice_command', (command) => {
      switch (command.type) {
        case 'play':
          onStart();  // Начало игры
          break;
        case 'pause':
          onPause();  // Пауза игры
          break;
        case 'stop':
          onStop();  // Остановка игры
          break;
        default:
          break;
      }
    });

    client.start();

    // Очистка при размонтировании
    return () => {
      client.stop();
    };
  }, [onStart, onPause, onStop]);

  return null;
};

export default VoiceControl;
