import React, { useRef, useEffect, useState, useCallback } from 'react';

const Pong = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);

  const [difficulty, setDifficulty] = useState('easy');
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState('');

  const playerScore = useRef(0);
  const botScore = useRef(0);

  const playerY = useRef(150);
  const [playerYState, setPlayerYState] = useState(150);

  const ball = useRef({
    x: 300,
    y: 200,
    dx: 4,
    dy: 4,
    radius: 7,
  });

  const bot = useRef({
    x: 580,
    y: 150,
    width: 10,
    height: 80,
  });

  const getBallSpeed = useCallback(() => (difficulty === 'hard' ? 7 : 4), [difficulty]);
  const getBotSpeed = useCallback(() => (difficulty === 'hard' ? 6 : 1), [difficulty]);

  const resetBall = useCallback(() => {
    const speed = getBallSpeed();
    const angle = Math.random() * Math.PI / 2 - Math.PI / 4;
    const direction = Math.random() > 0.5 ? 1 : -1;

    ball.current.x = 300;
    ball.current.y = 200;
    ball.current.dx = Math.cos(angle) * speed * direction;
    ball.current.dy = Math.sin(angle) * speed;
  }, [getBallSpeed]);

  const updateBot = useCallback(() => {
    const center = bot.current.y + bot.current.height / 2;
    const target = ball.current.y;
    const botSpeed = getBotSpeed();
    const offset = difficulty === 'easy' ? 30 : 10;

    if (Math.abs(center - target) > offset) {
      if (center < target) bot.current.y += botSpeed;
      else bot.current.y -= botSpeed;
    }

    bot.current.y = Math.max(0, Math.min(400 - bot.current.height, bot.current.y));
  }, [difficulty, getBotSpeed]);

  const update = useCallback(() => {
    const b = ball.current;
    b.x += b.dx;
    b.y += b.dy;

    // Столкновение с верхом и низом
    if (b.y - b.radius < 0 || b.y + b.radius > 400) {
      b.dy *= -1;
    }

    updateBot();

    // Столкновение с игроком
    if (b.x - b.radius < 30 && b.y > playerY.current && b.y < playerY.current + 80) {
      const relativeY = (playerY.current + 40) - b.y;
      const normalized = relativeY / 40;
      const angle = normalized * (Math.PI / 4);
      const speed = Math.sqrt(b.dx * b.dx + b.dy * b.dy);

      b.dx = Math.cos(angle) * speed;
      b.dy = -Math.sin(angle) * speed;

      if (b.dx < 0) b.dx = -b.dx;
    }

    // Столкновение с ботом
    if (
      b.x + b.radius > bot.current.x &&
      b.y > bot.current.y &&
      b.y < bot.current.y + bot.current.height
    ) {
      const relativeY = (bot.current.y + bot.current.height / 2) - b.y;
      const normalized = relativeY / (bot.current.height / 2);
      const angle = normalized * (Math.PI / 4);
      const speed = Math.sqrt(b.dx * b.dx + b.dy * b.dy);

      b.dx = -Math.cos(angle) * speed;
      b.dy = -Math.sin(angle) * speed;

      if (b.dx > 0) b.dx = -b.dx;
    }

    // Счёт
    if (b.x < 0) {
      botScore.current += 1;
      if (botScore.current >= 10) {
        setWinner('Bot Wins!');
        setGameOver(true);
        return;
      }
      resetBall();
    } else if (b.x > 600) {
      playerScore.current += 1;
      if (playerScore.current >= 10) {
        setWinner('Player Wins!');
        setGameOver(true);
        return;
      }
      resetBall();
    }
  }, [resetBall, updateBot]);

  const render = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillStyle = 'white';

    // Игрок
    ctx.fillRect(20, playerY.current, 10, 80);
    // Бот
    ctx.fillRect(bot.current.x, bot.current.y, bot.current.width, bot.current.height);

    // Мяч
    ctx.beginPath();
    ctx.arc(ball.current.x, ball.current.y, ball.current.radius, 0, Math.PI * 2);
    ctx.fill();

    // Счёт
    ctx.font = '30px Arial';
    ctx.fillText(`${playerScore.current}`, 200, 50);
    ctx.fillText(`${botScore.current}`, 400, 50);

    // Конец игры
    if (gameOver) {
      ctx.fillText('Game Over', 220, 180);
      ctx.fillText(winner, 210, 220);
    }
  };

  const gameLoop = useCallback(() => {
    if (!gameOver) {
      update();
      render();
      animationRef.current = requestAnimationFrame(gameLoop);
    } else {
      render(); // Показываем "Game Over"
    }
  }, [update, render, gameOver]);

  const startGame = useCallback((level) => {
    cancelAnimationFrame(animationRef.current);
    setDifficulty(level);
    setGameOver(false);
    setWinner('');
    playerScore.current = 0;
    botScore.current = 0;
    playerY.current = 150;
    bot.current.y = 150;
    setPlayerYState(150);
    resetBall();
    animationRef.current = requestAnimationFrame(gameLoop);
  }, [gameLoop, resetBall]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      const speed = 20;
      if (e.key === 'ArrowUp') {
        playerY.current = Math.max(0, playerY.current - speed);
      } else if (e.key === 'ArrowDown') {
        playerY.current = Math.min(400 - 80, playerY.current + speed);
      }
      setPlayerYState(playerY.current);
    };

    window.addEventListener('keydown', handleKeyDown);
    animationRef.current = requestAnimationFrame(gameLoop);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      cancelAnimationFrame(animationRef.current);
    };
  }, [gameLoop]);

  return (
    <div className="game-container">
      <h2>Pong Game</h2>
      <canvas
        ref={canvasRef}
        width="600"
        height="400"
        style={{ background: 'black', display: 'block', margin: 'auto' }}
      />
      <div style={{ marginTop: '10px', textAlign: 'center' }}>
        <button onClick={() => startGame('easy')}>Easy</button>
        <button onClick={() => startGame('hard')}>Hard</button>
        {gameOver && <button onClick={() => startGame(difficulty)}>Restart</button>}
        <p style={{ color: 'white' }}>
          Player: {playerScore.current} | Bot: {botScore.current}
        </p>
      </div>
    </div>
  );
};

export default Pong;
