// src/App.js
import React, { useState } from 'react';
import Pong from './Pong';
import VoiceControl from './VoiceControl';

const App = () => {
  const [gameStatus, setGameStatus] = useState('stopped');

  const startGame = () => {
    setGameStatus('playing');
  };

  const pauseGame = () => {
    setGameStatus('paused');
  };

  const stopGame = () => {
    setGameStatus('stopped');
  };

  return (
    <div>
      <h1>Pong with Voice Control</h1>
      {/* Компонент для голосового управления */}
      <VoiceControl onStart={startGame} onPause={pauseGame} onStop={stopGame} />
      {/* Игровой компонент с состоянием игры */}
      <Pong gameStatus={gameStatus} />
    </div>
  );
};

export default App;
