import React, { useRef, useEffect, useState } from 'react';
import { createAssistant, createSmartappDebugger } from '@salutejs/client';

const initializeAssistant = (getState, getRecoveryState) => {
  if (process.env.NODE_ENV === 'development') {
    return createSmartappDebugger({
      token: process.env.REACT_APP_TOKEN ?? '',
      initPhrase: `Запусти ${process.env.REACT_APP_SMARTAPP}`,
      getState,
      nativePanel: {
        defaultText: '',
        screenshotMode: false,
        tabIndex: -1,
      },
    });
  } else {
    return createAssistant({ getState });
  }
};

export class Pong extends React.Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
    this.state = {
      ballSpeedX: 4,
      ballSpeedY: 4,
      ballPosX: 50,
      ballPosY: 50,
      paddle1Y: 150,
      paddle2Y: 150,
      paddleHeight: 100,
      paddleSpeed: 30,
      score1: 0,
      score2: 0,
    };
  }

  componentDidMount() {
    this.canvas = this.canvasRef.current;
    this.ctx = this.canvas.getContext('2d');
    this.startGame();
  }

  startGame = () => {
    this.updateGame();
    this.interval = setInterval(this.updateGame, 1000 / 60); // 60 FPS
  };

  updateGame = () => {
    this.moveBall();
    this.detectCollisions();
    this.draw();
  };

  moveBall = () => {
    const { ballSpeedX, ballSpeedY, ballPosX, ballPosY } = this.state;

    if (ballPosX + ballSpeedX > this.canvas.width || ballPosX + ballSpeedX < 0) {
      this.setState(prevState => ({
        ballSpeedX: -prevState.ballSpeedX,
        score1: ballPosX + ballSpeedX > this.canvas.width ? prevState.score1 : prevState.score1 + 1,
        score2: ballPosX + ballSpeedX < 0 ? prevState.score2 : prevState.score2 + 1
      }));
    }
    if (ballPosY + ballSpeedY > this.canvas.height || ballPosY + ballSpeedY < 0) {
      this.setState(prevState => ({
        ballSpeedY: -prevState.ballSpeedY
      }));
    }

    this.setState(prevState => ({
      ballPosX: prevState.ballPosX + ballSpeedX,
      ballPosY: prevState.ballPosY + ballSpeedY,
    }));
  };

  detectCollisions = () => {
    const { ballPosX, ballPosY, paddle1Y, paddle2Y, paddleHeight, canvas } = this.state;

    // Paddle collision
    if (ballPosX < 10 && ballPosY > paddle1Y && ballPosY < paddle1Y + paddleHeight) {
      this.setState(prevState => ({
        ballSpeedX: -prevState.ballSpeedX
      }));
    }
    if (ballPosX > canvas.width - 10 && ballPosY > paddle2Y && ballPosY < paddle2Y + paddleHeight) {
      this.setState(prevState => ({
        ballSpeedX: -prevState.ballSpeedX
      }));
    }
  };

  draw = () => {
    const { ballPosX, ballPosY, paddle1Y, paddle2Y, paddleHeight, score1, score2 } = this.state;

    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
    this.ctx.fillStyle = 'black';

    // Draw ball
    this.ctx.beginPath();
    this.ctx.arc(ballPosX, ballPosY, 10, 0, Math.PI * 2);
    this.ctx.fill();

    // Draw paddles
    this.ctx.fillStyle = 'white';
    this.ctx.fillRect(0, paddle1Y, 10, paddleHeight); // Left paddle
    this.ctx.fillRect(this.canvas.width - 10, paddle2Y, 10, paddleHeight); // Right paddle

    // Draw scores
    this.ctx.font = '30px Arial';
    this.ctx.fillText(score1, this.canvas.width / 4, 30);
    this.ctx.fillText(score2, (this.canvas.width / 4) * 3, 30);
  };

  movePaddle = (event) => {
    const { paddleHeight, paddleSpeed, canvas } = this.state;
    if (event.key === 'ArrowUp' && this.state.paddle2Y > 0) {
      this.setState(prevState => ({ paddle2Y: prevState.paddle2Y - paddleSpeed }));
    }
    if (event.key === 'ArrowDown' && this.state.paddle2Y + paddleHeight < canvas.height) {
      this.setState(prevState => ({ paddle2Y: prevState.paddle2Y + paddleSpeed }));
    }
    if (event.key === 'w' && this.state.paddle1Y > 0) {
      this.setState(prevState => ({ paddle1Y: prevState.paddle1Y - paddleSpeed }));
    }
    if (event.key === 's' && this.state.paddle1Y + paddleHeight < canvas.height) {
      this.setState(prevState => ({ paddle1Y: prevState.paddle1Y + paddleSpeed }));
    }
  };

  render() {
    return (
      <div>
        <canvas
          ref={this.canvasRef}
          width="800"
          height="600"
          onKeyDown={this.movePaddle}
          tabIndex="0"
        />
      </div>
    );
  }
}
