// src/App.js
import React, { useState } from 'react';
import Pong from './Pong'; // Компонент игры Pong
import VoiceControl from './VoiceControl'; // Компонент голосового управления

const App = () => {
  const [gameStatus, setGameStatus] = useState('старт');  // Состояние игры

  // Функции для управления состоянием игры
  const handleStart = () => {
    setGameStatus('игра');
  };

  const handlePause = () => {
    setGameStatus('пауза');
  };

  const handleStop = () => {
    setGameStatus('стоп');
  };

  return (
    <div>
      <h1>Игра Pong с голосовым управлением</h1>
      {/* Компонент для голосового управления */}
      <VoiceControl onStart={handleStart} onPause={handlePause} onStop={handleStop} />
      
      {/* Компонент игры с передачей состояния */}
      <Pong gameStatus={gameStatus} />
    </div>
  );
};

export default App;
